sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    // Modified the if to return if the newValue == oldValue to avoid
    // unecessary trips to the server
    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }

    // Instantiate the GetEmailAddress Script Include 
    var getCountryC = new GlideAjax('GetCountryCode');
    // Specify the getEmail method
    getCountryC.addParam('sysparm_name','getCountry');
    // Pass the Requested for sys_id
    getCountryC.addParam('sysparm_userID', g_form.getValue('u_requested_for'));
    // Send the request to the server
    getCountryC.getXML(populateCountryCode);


    // When the response is back from the server
    function populateCountryCode(response){
        // Extract the email address from the response, clear any value from the email field, 
        // set new value in the email field
        var countryFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
        g_form.clearValue('x_58872_needit_needit.u_string_1');
        g_form.setValue('x_58872_needit_needit.u_string_1',countryFromScriptInclude);
    }
}
